fn main() {
    // Create variables to store the final floor and the position of the instruction that first makes Santa enter the basement
    let mut final_floor: i64 = 0;
    let mut first_basement_pos: usize = 0;

    let mut is_first_basement_visit: bool = true;

    // Loop over the characters to get part one "To what floor do the instructions take Santa?" and
    // Part two "What is the position of the character that causes Santa to first enter the basement?"
    for (i,  c) in INSTRUCTIONS.chars().enumerate(){
        if c == '('{
            final_floor += 1;
        } else{
            final_floor -= 1;
        }

        if final_floor == -1 && is_first_basement_visit{
            first_basement_pos = i + 1;
            is_first_basement_visit = false;
        }
    }

    println!("Final Floor: {}\nFirst Basement Position: {}", final_floor, first_basement_pos)
}
